--
-- Tabellenstruktur für Tabelle `main_menu_dynamic_content`
--

CREATE TABLE `main_menu_dynamic_content` (
  `id` int(11) NOT NULL,
  `content` text,
  `priority` int(11) DEFAULT NULL,
  `display` tinyint(1) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

--
-- Daten für Tabelle `main_menu_dynamic_content`
--

INSERT INTO `main_menu_dynamic_content` (`id`, `content`, `priority`, `display`) VALUES
(1, '\r\n\r\nHier könnte Ihre Werbung stehen!', 2, 0),
(2, 'Bunkers And Badasses V0.1.0\r\n\r\n\r\nDas Mainmenü funktioniert!!!\r\n\r\nP.S.: Wer Rechtschreibfehler findet darf sie behalten!', 1, 0),
(3, 'Version 0.2.0 von Bunkers and Badasses ist fertig!!!\r\n\r\nDamit ist die erste Demo-Version, in der die Basis-Prinzipien des Spiels ausprobiert werden können fertig.\r\n\r\nLeider funktionieren noch nicht alle Kampfsysteme (zumindest die Berechnungen sind nicht immer den Regeln nach korrekt) und die Resourcen und Punkte sind nicht angepasst.\r\n\r\nAber immerhin können alle wichtigen Spielfunktionen ausprobiert werden.\r\n\r\nDer Kampf um den Titel des ULTIMATIVEN SUPER BADASS ALLER BADASSES muss also leider noch etwas warten. Aber trotzdem kann man nich schonmal mit dem Spiel vertraut machen.', 1, 0),
(4, '09.09.17: Update 0.3.0 ist da!!!\r\n\r\nJetzt mit einem verbesserten Kampfsystem für mehr Tote auf dem Schlachtfeld!!! \r\nAußerdem noch einige neue Grafiken, Verbesserungen und Bugfixes.', 1, 0),
(5, '30.12.17: Update 0.4.0 ist fertig!\r\n\r\nDamit können begonnene Spiele jetzt automatisch gespeichert werden und fortgesetzt werden, wenn sie nicht bis zum Ende gespielt werden.\r\nAußerdem sind wieder viele Verbesserungen und Bugfixes vorhanden.', 2, 0),
(6, '20.01.18: Update 0.5.0 ist fertig!\r\n\r\nDamit werden von allen Spielen jetzt automatisch Statistiken angelegt, um den ULTIMATIVEN SUPER BADASS ALLER BADASSES zu bestimmen.', 3, 0),
(7, 'Version 1.0.0 ist endlich Fertig!!!\n\nNach nur 1.5 Jahren Entwicklungszeit ist Version 1.0.0 von Bunkers and Badasses endlich Fertig. Mit allen dazugehörigen Funktionen, Features und noch viel mehr Bullshit!\nDer Ermittlung des ULTIMATIVEN SUPER BADASS ALLER BADASSES steht also nichts mehr im Weg!\n\nIn diesem Sinne... Viel Spaß beim Spielen!', 4, 1);

